$(function(){$.fn.materialForm=function(){this.find("input, textarea").each(function(){var e=$(this).attr("type");if(e!="hidden"&&e!="submit"){var t=$(this).attr("placeholder");var n=$(this).wrap("<div class='input'></div>").parent();var r=$(this).prop("tagName").toLowerCase();n.addClass(r);n.append("<span class='bar'></span>");n.append("<label>"+t+"</label>");$(this).removeAttr("placeholder")}});this.find("input, textarea").on("blur",function(){var e=$(this).attr("type");if(e!="hidden"&&e!="submit"){if($(this).val())$(this).addClass("filled");else $(this).removeClass("filled")}})}})