$(function(){$.fn.materialForm=function(){this.find("input, textarea").each(function(){var e=$(this).attr("placeholder");var t=$(this).wrap("<div class='input'></div>").parent();var n=$(this).prop("tagName").toLowerCase();t.addClass(n);t.append("<span class='bar'></span>");t.append("<label>"+e+"</label>");$(this).removeAttr("placeholder")});this.find("input, textarea").on("blur",function(){if($(this).val())$(this).addClass("filled");else $(this).removeClass("filled")})}})